import React, {useLayoutEffect, useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View, SafeAreaView, KeyboardAvoidingView, ScrollView, Platform,  TextInput  } from 'react-native'
import { Avatar } from 'react-native-elements'
import { AntDesign, FontAwesome, Ionicons } from '@expo/vector-icons'

const ChatScreen = ({navigation, route }) => {

    const [input, setInput] = useState("");

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Chat",
            headerBackTitleVisible: false,
            headerTitleAlign: "left",
            headerTitle: () => (
                <View style={{
                    flexDirection:"row",
                    alignItems:"center",
                    marginLeft:-20,
                }}>
                    <Avatar rounded source={{ uri: "https://ampelou.s3.amazonaws.com/default.png",}}/>
                    <Text style={{color:"white", fontWeight:"700", marginLeft:10, fontSize:15, textAlign:'center'}}>{route.params.chatName}</Text>
                </View>
            ),
            headerRight: () => (
                <View style={{
                    flexDirection: "row",
                    justifyContent: "space-between",
                    marginRight: 20,
                    width:80,
                }}>
                    <TouchableOpacity>
                        <FontAwesome name="video-camera" size={24} color="white"/>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Ionicons name="call" size={24} color="white"/>
                    </TouchableOpacity>
                </View>
            )
        })
    }, [navigation]);

    const sendMessage = () =>{

    }

    return (
        <SafeAreaView style={{flex:1, backgroundColor:"#f6f6f6"}}>
            <StatusBar style="dark" />
            <KeyboardAvoidingView 
            behavior={Platform.OS === "ios" ? "padding":"height"}
            style={styles.container}
            keyboardVerticalOffset={90}>
                <>
                    <ScrollView>

                    </ScrollView>
                    <View style={styles.footer}>
                        <TextInput value={input} onChangeText={(text) => setInput(text)} placeholder="Type Message..." style={styles.TextInput} />
                        <TouchableOpacity onPress={sendMessage} activeOpacity={0.5}>
                            <Ionicons name="send" size={24} color="#0b3c6e"/>
                        </TouchableOpacity>
                    </View>
                </>
            </KeyboardAvoidingView>
        </SafeAreaView>
    )
}

export default ChatScreen

const styles = StyleSheet.create({
    container:{
        flex:1,
    },
    footer:{
        flexDirection:"row",
        alignItems:"center",
        width:"100%",
        padding:15,
    },
    textInput:{
        bottom:0,
        height:40,
        flex:1,
        marginRight:15,
        borderColor:"black",
        borderBottomColor:"#ECECEC",
        borderWidth:1,
        padding:10,
        color:"black",
        borderRadius:30,
    }
})
