import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View } from 'react-native';
import { ListItem, Avatar } from 'react-native-elements';
import {db,auth} from "../firebaseConfig"
const CustomListItem = ({id, chatName, enterChat}) => {
    const [chatMessages, setChatMessages] = useState([]);

    useEffect (() => {
        const unsubscribe = db.collection("chats").doc(id).collection('messages').orderBy('timestamp', 'desc').onSnapshot((snapshot) => setChatMessages(snapshot.docs.map((doc) => doc.data()))
        );
        return unsubscribe;
    })

    return (
        <ListItem key={id} onPress={() => enterChat(id, chatName)} key={id} bottomDivider>
            <Avatar rounded source={{ uri: chatMessages?.[0]?.photoURL || "https://ampelou.s3.amazonaws.com/default.png",}} containerStyle={styles.avatar} />
            <ListItem.Content>
                <ListItem.Title style={{fontWeight:"800"}}>
                   {chatName}
                </ListItem.Title>
                <ListItem.Subtitle numberOfLines={1} ellipsizeMode="tail">
                    {chatMessages?.[0]?.displayName}: {chatMessages?.[0]?.message}
                </ListItem.Subtitle>
            </ListItem.Content>
        </ListItem>
    );
}; 

export default CustomListItem

const styles = StyleSheet.create({
    avatar :{
        resizeMode: 'cover',
    }
})
