import React, {useLayoutEffect, useState} from 'react'
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View, SafeAreaView, KeyboardAvoidingView, ScrollView, Platform,  TextInput,Keyboard, TouchableWithoutFeedback } from 'react-native'
import { Avatar } from 'react-native-elements'
import { AntDesign, FontAwesome, Ionicons } from '@expo/vector-icons'
import { db, auth } from '../firebaseConfig';
import firebase from 'firebase/app';


const ChatScreen = ({navigation, route }) => {

    const [input, setInput] = useState("");

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Chat",
            headerBackTitleVisible: false,
            headerTitleAlign: "left",
            headerTitle: () => (
                <View style={{
                    flexDirection:"row",
                    alignItems:"center",
                    marginLeft:-20,
                }}>
                    <Avatar rounded source={{ uri: "https://ampelou.s3.amazonaws.com/default.png",}}/>
                    <Text style={{color:"white", fontWeight:"700", marginLeft:10, fontSize:15, textAlign:'center'}}>{route.params.chatName}</Text>
                </View>
            ),
            headerRight: () => (
                <View style={{
                    flexDirection: "row",
                    justifyContent: "space-between",
                    marginRight: 20,
                    width:80,
                }}>
                    <TouchableOpacity>
                        <FontAwesome name="video-camera" size={24} color="white"/>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Ionicons name="call" size={24} color="white"/>
                    </TouchableOpacity>
                </View>
            )
        })
    }, [navigation]);

    const sendMessage = () =>{
        Keyboard.dismiss();
        db.collection('chats').doc(route.params.id).collection('messages').add({
            timestamp: firebase.firestore.FieldValue.serverTimestamp(),
            message: input,
            displayName: auth.currentUser.displayName,
            email: auth.currentUser.email,
            photoURL: anth.currentUser.photoURL,
        });
        setInput('');
    };


    return (
        <SafeAreaView style={{flex:1, backgroundColor:"f9f9f9"}}>
            <StatusBar style="dark" />
            <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding":"height"} style={styles.container} keyboardVerticalOffset={90}>

                <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
                <>
                    <ScrollView>

                    </ScrollView>
                    <View style={styles.footer}>
                        <TextInput value={input} onChangeText={(text) => setInput(text)} onSubmitEditing={(sendMessage)} placeholder="Type Message..." style={styles.textInput} />
                        <TouchableOpacity onPress={sendMessage} activeOpacity={0.5}>
                            <Ionicons name="send" size={24} color="#0b3c6e"/>
                        </TouchableOpacity>
                    </View>
                </>
                </TouchableWithoutFeedback>
            </KeyboardAvoidingView>
        </SafeAreaView>
    )
}

export default ChatScreen

const styles = StyleSheet.create({
    container:{
        flex:1,
    },
    footer:{
        flexDirection:"row",
        alignItems:"center",
        width:"100%",
        padding:10,
    },
    textInput:{
        flex:1,
        bottom:0,
        height:40,
        marginRight:10,
        borderColor:"black",
        backgroundColor:"#ECECEC",
        borderWidth:1,
        padding:5,
        color:"black",
        borderRadius:30,
        width:"100%",
    },
})
