import React, {useLayoutEffect} from 'react'
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View, SafeAreaView, KeyboardAvoidingView } from 'react-native'
import { Avatar } from 'react-native-elements'
import { AntDesign, FontAwesome, Ionicons } from '@expo/vector-icons'

const ChatScreen = ({navigation, route }) => {

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Chat",
            headerBackTitleVisible: false,
            headerTitleAlign: "left",
            headerTitle: () => (
                <View style={{
                    flexDirection:"row",
                    alignItems:"center",
                    marginLeft:-20,
                }}>
                    <Avatar rounded source={{ uri: "https://ampelou.s3.amazonaws.com/default.png",}}/>
                    <Text style={{color:"white", fontWeight:"700", marginLeft:10, fontSize:15, textAlign:'center'}}>{route.params.chatName}</Text>
                </View>
            ),
            headerRight: () => (
                <View style={{
                    flexDirection: "row",
                    justifyContent: "space-between",
                    marginRight: 20,
                    width:80,
                }}>
                    <TouchableOpacity>
                        <FontAwesome name="video-camera" size={24} color="white"/>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Ionicons name="call" size={24} color="white"/>
                    </TouchableOpacity>
                </View>
            )
        })
    }, [navigation]);

    return (
        <SafeAreaView style={styles.container}>
            <StatusBar style="dark" />
            <Text>{route.params.chatName}</Text>
        </SafeAreaView>
    )
}

export default ChatScreen

const styles = StyleSheet.create({
    container:{
        backgroundColor:"#f6f6f6",
        flex:1,
    }
})
